// led cavern
//
// modification of the original shader https://www.shadertoy.com/view/DdsBzl - which is a really neat trick.
//

float str = 2.0;

vec2 warp(vec2 inp)
{
    inp.y -= (inp.y - .5)* str * abs(inp.x - .5);
    return inp;
}

vec4 bn(vec2 uv)
{
    float ft = fract(iTime);
    float t = 37.5f;

    t = t + iTime;

    float scl = 0.5f;
    vec2 off = vec2(cos(t*3.14*scl), sin(t*3.14*scl)) + scl*scl*t*100.0f;

    return( textureLod(iChannel0, (uv * iResolution.xy + 0.5f + off ) / 1024.0f * scl, 0.0f) );
}

vec3 col(vec2 inp)
{
    vec4 n = bn(inp);

    return pow(n.rgb, vec3(500.0f * n.a*n.a)) * ((1.0f - sin(inp.y * iResolution.y * n.a) - 1.0f - sin(inp.x * iResolution.x * n.a)) * 0.5f + 0.5f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = warp(fragCoord/iResolution.xy);
    fragColor = vec4(col(uv), 1);
}
