void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy - iResolution.xy / 2.0)/min(iResolution.x, iResolution.y);

    uv *= 5.;

    int value = 5;

    float l;

    for(int i = 0; i < value; i++)
    {
        l = length( vec2(uv.x, uv.y) );

        uv.x = uv.x - cos(uv.y + sin(l)) + sin(iTime / 5.0);
        uv.y = uv.y - sin(uv.x + cos(l)) + cos(iTime / 2.0);

        fragColor = vec4(sin(l * 6.0), sin(l * 4.0), sin(l * 2.0), 1.0);

    }

}
