void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec4 a = texture(iChannel0,u);
    fragColor = (vec4(length(a.xy),a.xy,0)*.4+.5)*pow(a.z,.3)*.8;
    //fragColor = a;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  r = vec4(0);
    vec4  a = A(u);
    float z = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        float l = length(vec2(i,j));
        if((i==0.&&j==0.)|| l>z-.1){continue;}
        vec2  c = normalize(vec2(i,j));
        vec4  a2= A(u+vec2(i,j));
        vec2  b = a2.xy-a.xy;
        float d = length(a2.xy)-length(a.xy)*1.15;
        r.xy += c*abs(d)*exp(-l*l*.16);
        r.xy += c*dot(c,b)*exp(-l*l*.16)*.8;
    }}
    fragColor = r;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec2 v = u/iResolution.xy;
    vec4 t = A(u);
    vec2 m = +t.xy
             +B(u).xy*.25
             -float(v.x<.01)*vec2(1,0)  //wall
             -float(v.y<.01)*vec2(0,1)  //wall
             +float(v.x>.99)*vec2(1,0)  //wall
             +float(v.y>.99)*vec2(0,1); //wall;
    float s = 0.;
    float z = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        if(length(vec2(i,j))>z-.1){continue;}
        vec2 c = (m+vec2(i,j));
        s += exp(-dot(c,c));
    }}
    if(s==0.){s = 1.;}
    s = 1./s;

    fragColor = vec4(m,s,0);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float tz = 0.;
    vec4  a  = vec4(0);
    float z  = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        if(length(vec2(i,j))>z-.1){continue;}
        vec4 t = A(u+vec2(i,j));
        vec4 m = B(u+vec2(i,j));
        vec2 c = (m.xy-vec2(i,j));
        float z = t.z*exp(-dot(c,c));
        a.xy += z*m.xy;
        a.z  += z*m.z;
        tz   += z;
    }}
    if(tz==0.){tz = 1.;}
    a.xy /= tz;
    //a = A(u);
    if(iMouse.z>0.)
    {
        vec2 m = 22.*(u-iMouse.xy)/450.;
        a += vec4(m*.5,0,0)*exp(-dot(m,m));
    }
    float keyW = texture( iChannel2, vec2(87.5/256.,.25) ).x;
    if(iFrame==0||keyW!=0.)
    {
        //vec2 m = 4.*(u*2.-iResolution.xy)/iResolution.y;
        a = vec4(0,0,1,0);//-vec4(m,0,0)*exp(-dot(m,m));
    }
    fragColor = a;
}
