void mainImage( out vec4 c, vec2 p )
{
    vec2 uv = (p.xy / iResolution.xy) * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    // Use the sin and cos functions to create a colorful pattern
    float r = sin(uv.x * 10.0 + iTime) * 0.5 + 0.5;
    float g = cos(uv.y * 10.0 + iTime) * 0.5 + 0.5;
    float b = tan(uv.x * uv.y + iTime) * 0.5 + 0.5;

    // Use the mod function to create a repeating pattern
    r = mod(r * 50.0 + iTime, 2.0);
    g = mod(g * 50.0 + iTime, 2.0);
    b = mod(b * 50.0 + iTime, 2.0);

    c = vec4(r, g, b, 1.0);
}
