void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 normalizedCoord = fragCoord /iResolution.xy * 2.0 - 1.0;
    float angle = iTime * 0.3;
    for (float i = 0.0 ; i < 32.0; i += 2.0) {
    normalizedCoord = abs(normalizedCoord);
    normalizedCoord -= 0.4;
    normalizedCoord *= 1.2;
    normalizedCoord *= mat2 (
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
    }
    fragColor = vec4(0.9*length(normalizedCoord), 0.2*length(normalizedCoord), 0.4*length(normalizedCoord), 1.0);
}
