void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // centeralize uv coordinates and scale to aspect ratio
    fragCoord = (fragCoord * iResolution.x/iResolution.y)/iResolution.xy;
    vec2 uv = (fragCoord - 1.0) * 2.0;
    vec2 z;
    float n;
    float B = 256.0;

    for(int i = 0; i < 200; i++)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + uv;
        //z = z * sin(iTime)*0.5;
        if( dot(z,z)>(B*B) ) break;
        n += 1.0;
    }
    float sn = n - log2(log2(dot(z,z))) + 4.0;
    vec3 col = vec3(sn, sn, 0);

    // Output to screen
    fragColor = vec4(col,1);
}
