mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0.);

    float i,j,d=.2,e;
    for (i=0.;i++<64.&&d>1e-4;){
    vec3 p=vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.y,e);
    p=vec3(log(length(p)),sin(p.z),atan(p.x,p.y))/.1;
    p.yz*=rotate2D(1.41);
    p.xz*=rotate2D(iTime*.4);
    for(j=0.;j++<16.;)
        p=abs(abs(p)-.1)-.05,
        d=pow(dot(cos(p.xz),sin(p.yz)),1.)/4.,
        d-=dot(p,p)/2e4;
    e+=d;
    }fragColor+=vec4(hsv(sqrt(i/j),.7,6.),1)/i;

}
