void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-iResolution.xy/2.)/min(iResolution.x,iResolution.y)*2.;
    //uv /= (iTime+1.);
    uv /=exp(iTime);
    uv+=vec2(-.7290105,.2101465);


    vec2 z = vec2(0);

    for (int x = 0; x < 2048; x++){
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + uv;
    }

    vec3 col = vec3(exp(length(z))); // <- float magic, ga ne razumem
    col = max(vec3(smoothstep(.95,1.,fract(uv*3.)),0.), col);
    fragColor = vec4(col ,1.0);
}
