void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float d = 0.0;
    float a = 0.463647609;
    uv.x = abs(uv.x);
    if (uv.x * 0.5 + uv.y > 0.2) {
        uv -= vec2(0.2, 0.1);
        uv *= mat2(cos(a), -sin(a), sin(a), cos(a));
        uv /= vec2(0.22360679, 0.3);
        d = length(uv);
    } else if (uv.y < 0.1) {
        uv.y -= 0.2;
        uv *= mat2(cos(a), -sin(a), sin(a), cos(a));
        uv /= vec2(0.22360679, 0.3);
        d = length(uv) - 1.0;
    }
    float r = smoothstep(1.4, 1.2, d) - smoothstep(1.2, 1.0, d);
    vec3 col = vec3(r, 0., 0.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
