void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = iTime * 0.1;
    vec2 normalizedCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    for (float i = 0.0; i < 32.0; i += 1.0){
        normalizedCoord = abs(normalizedCoord); 
        normalizedCoord -= 0.5;
        normalizedCoord *= 1.1;
        normalizedCoord *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
         );
    }
    fragColor = vec4(length(normalizedCoord),
    length(normalizedCoord + vec2(0.1, -0.7)),
    length(normalizedCoord + vec2(-0.8, -0.6)),2.0);
}
