#define MAX_ITERATIONS 80.

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 r = iResolution.xy, uv = fragCoord.xy/r.y-vec2(.5*r.x/r.y-.5,0.);

    float R = 2., t = iTime / 2.;

    vec2 c;
    if (iMouse.z > 0.) {
        c = (iMouse.xy / iResolution.xy - .5) * 2.;
    } else {
        float tA = .8, tB = 20., g = mod(t, tA + tB + tA), a = (g < tA) ? g/tA*3.14/2. : (g < tA + tB) ? 3.14/2.+(g-tA)/tB*3.14 : (g-tA-tB)/tA*3.14/2.+3.*3.14/2.;
        c = vec2(cos(a), sin(a)) * .75;
    }

    if (length(fragCoord.xy - (c * .5 + .5) * iResolution.xy) < 10.) {
        fragColor = vec4(1., 0., 0., 0.);
        return;
    }

    float S = 1.8; // CHANGE FOR SCALE
    vec2 z = (uv - .5) * S;

    float i = 0.;
    for (; (z.x * z.x + z.y * z.y) < R * R && i < MAX_ITERATIONS; ++i) {
        float xtemp = z.x * z.x - z.y * z.y;
        z.y = 2. * z.x * z.y + c.y;
        z.x = xtemp + c.x;
    }

    if (i == MAX_ITERATIONS) {
        fragColor = vec4(vec3(0.), 1.);
    } else {
        float j = i / MAX_ITERATIONS;
        fragColor = vec4(.5+.6*(clamp(abs(mod(j*6.+vec3(0.,4.,2.),6.)-3.)-1.,0.,1.)-.5),1.);
    }
}
