vec2 noise(in vec2 uv)
{
    //return sin(uv.x/1.6)+cos(-uv.y*1.6);
    return vec2(sin(uv.x),cos(uv.y))+vec2(1.); }

#define OCTAVES  8   // 9
float fbm1(in vec2 uv,int octaves,float i1,float value1)
{
    //this function generates the terrain height

     float f = 2.15*0.9; //change this number to change the spacing of the mountains 
    //Another interesting variation: case  BB
  //  float f = 2.15+(sin(uv.x/i1)+cos(uv.x/i1))*value1;

    float value = value1;      float amplitude = i1*value1;     float freq = i1/f;
    float n1 = 0.;        vec2 noise2,noise1;
    for (int i = 0; i < octaves; i++)
    {      
        noise2 = noise(noise1.yx+uv/freq);
        noise1 = noise2-vec2(value)-noise1.yx*freq;
        freq /= -f;
        n1 = noise1.x+noise1.y;

        value += n1*(amplitude-freq);

        uv += noise2.yx*freq;      }     return value/3.5; }

float fbmV(vec2 uv, int oct){    float result = 0.;
    //uv /= 2.;
    for(float i = 1.; i < 3.; i++){
        float i1 = i*i;        result -= fbm1(uv,oct,i1,result/i1);    oct /= 2;
    }
    return result;  }

float fbmValley(in vec2 uv){    return fbmV(uv,OCTAVES); } // => terrain(vec3 p)
