/// Simple noise algorithm by Trisomie21
float snoise( vec2 p ) {
    p.y-=hashseed*100.;
    p.xy=p.yx;
	vec2 f = fract(p);
	p = floor(p);
	float v = p.x+p.y*1000.0;
	vec4 r = vec4(v, v+1., v+1000.0, v+1001.);
	r = fract(12345.*sin(r*.001));
	f = f*f*(3.0-2.0*f);
	return 2.0*(mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y))-1.0;
}


