float terrain(vec3 p) {
    if (p.y>2.) return p.y+2.;
    p.z+=time*3.;
    float ini=smoothstep(100.,70.,p.z);
      p.x+=ini*4.5;         // water level apear   //  delete  // =>plat white valley
    p.y+=.4+smoothstep(120.*3.,130.*3.,p.z)*.65;
    p.x-=path(p.z);

//	h = terrain(p.xz*vec2(.5,1.)) * 1.2;         // default
    h =     fbmValley(p.xz*vec2(1.,1.))  *1.2 ;  // Glacial valleys

	h += smoothstep(-0.3, 1.5, h);
//    h*=1.-ini*.7;
	float ap=.3+max(0.,sin(p.z*.05+cos(p.z*.123)*.0))*.5;
       // case AA
    h=mix(h*0.9,-1.*2.+1.3,exp(-ap*(abs(p.x-.5*snoise(p.zx*.5)))));

    //case BB
 //   h=mix(h*0.9,-1.+1.3*0.,exp(-ap*(/*abs*/(p.x-.5*snoise(p.zz*.5)))));

   // case CC  (like voronoy  (cut top of mountains)
   //   h=mix(h*0.9,-1.*2.+1.3,exp( 0.4*(abs(abs(p.x-.5*snoise(p.zx*.5) -p.x*2.) )   ) ));   

	float d = p.y - h;	
    tpos=p;
	return d*.5;
}

