float MapMissile(vec3 p)
{
  float d= fCylinder( p, 0.70, 1.7); float dMin=1000.  ; vec3 q;
  if (d<1.0)
  {
    missileDist = min(missileDist, fCylinder( p, 0.12, 1.2));   
