float MapTailFlap(vec3 p, float mirrored)
{      float d, dMin=100000.0;  vec3 q ;
  p.z+=0.3;
  pR(p.xz, rudderAngle*(-1.*mirrored)); 
  p.z-=0.3;

  float tailFlap =sdBox(p- vec3(0., -0.04, -.42), vec3(0.025, .45, .30));

  // tailFlap front cutout
  q =checkPos ;
