/////////////////////////////////////////////////  fantastic terrainColors start
vec3 terrainColors /*lighting*/(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1.5) ; //  def  vec3(1., .956, .839);

 //   vec3 albedo = vec3(1.09);      // case AA Default    White glacial valleys
     vec3 albedo =vec3(abs(cos(iTime*0.16 )),abs(sin(iTime*0.2)), 
                      1.+sin(iTime*0.2) ) ;  //case BB     Fantastic Valleys

   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0.9, .96, 1.) * .1;
    //col += amb * .2;

   	return col;
}

