highp float hash(
	highp vec2 x){
return fract(sin(dot(
	x,vec2(11,57)))*4e3);
	}

highp float star(
	highp vec2 x){
x *= mat2(cos(0.5),-sin(0.5),sin(0.5),cos(0.5));
x.y += iTime*16.0;
highp float shape = (1.0-length(
	fract(x-vec2(0,0.5))-0.5));
x *= vec2(1,0.1);
highp vec2 fr = fract(x);
highp float random = step(hash(floor(x)),0.01),
	        tall = (1.0-(abs(fr.x-0.5)+fr.y))*random;
return clamp(clamp((shape-random)*step(hash(
	floor(x+vec2(0,0.05))),0.01),0.0,1.0)+tall,0.0,1.0);
	}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy*0.5)/(
    iResolution.x, iResolution.y);

    fragColor = pow(vec4(star(uv*24.0))*1.1,
                    vec4(16,6,4,1));
}
