void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Calculate the angle and distance from the center
    float angle = atan(uv.y, uv.x);
    float radius = length(uv);

    // Spiral settings
    float spiralWidth = 0.05;
    float numSpirals = 5.0;

    // Animate spirals with different speeds and directions
    float spiral1 = smoothstep(spiralWidth, -spiralWidth, cos((angle + iTime) * numSpirals - 3.0 * radius));
    float spiral2 = smoothstep(spiralWidth, -spiralWidth, cos((-angle - iTime * 0.5) * numSpirals * 0.5 - 3.0 * radius * 0.5));
    float spiral3 = smoothstep(spiralWidth, -spiralWidth, cos((angle + iTime * 0.75) * numSpirals * 0.75 - 3.0 * radius * 0.75));
    float spiral4 = smoothstep(spiralWidth, -spiralWidth, cos((-angle - iTime * 1.25) * numSpirals * 1.25 - 3.0 * radius * 1.25));

    // Change the spiral colors over time
    vec3 color1 = mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 0.0, 1.0), 0.5 * (sin(iTime) + 1.0));
    vec3 color2 = mix(vec3(0.0, 1.0, 0.0), vec3(1.0, 1.0, 0.0), 0.5 * (cos(iTime) + 1.0));
    vec3 color3 = mix(vec3(1.0, 0.5, 0.0), vec3(0.5, 0.0, 0.5), 0.5 * (sin(iTime + 1.57) + 1.0));
    vec3 color4 = mix(vec3(0.0, 0.5, 1.0), vec3(0.5, 1.0, 0.5), 0.5 * (cos(iTime + 1.57) + 1.0));

    // Create the pulsating background
    float backgroundFrequency = 30.0; // 7 FPS
    float backgroundPulse = step(0.5, sin(iTime * backgroundFrequency * 10.0 * 3.14159));

    // Combine spirals and background
    vec3 finalColor = (color1 * spiral1 + color2 * spiral2 + color3 * spiral3 + color4 * spiral4) * (1.0 - backgroundPulse) + backgroundPulse * vec3(0.1, 0.1, 0.1);

    fragColor = vec4(finalColor, 1.0);
}
