#define r iResolution
void mainImage(out vec4 o, vec2 u) {    
    u = (u - r.xy / 2.) / r.y; o -= o;

    float i, a, t = iTime * .5; 
    vec3 p, q;

    while(i++ < 100.)
        a = floor(t) + i / 10.,
        p = vec3( cos(3. * t + 6. * sin(a)) * .1,
                  sin(2. * t + 6. * cos(a)) * .1, 
                  1. - fract(t + i / 100.)),

        q = cross(vec3(u, 1), p),
        o += smoothstep(.035, 0., length(q) - .01) 

                * ( cos(a * 2.) > .5
                    ? vec4(8, 4, 1, 0)
                    : vec4(2) // smoke
                  ) / length(p) / 80.;
}
