void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float aspect = iResolution.x/iResolution.y;
    float x = sin(4.75+uv.x*3.1415*2.);
    float y = sin(uv.y*3.1415*2./aspect);

    vec2 Res = vec2(320,200);

    // Output to screen
    fragColor = vec4(sin(uv.x+iTime) > cos(uv.y) ? vec3(abs(1),0,0) : vec3(0,0,abs(1)), 1.0);

    float G = 8.;
    int GMask = int(G-1.);

    vec2 g = round(fragCoord/G);
    float t = iTime*4.;
    float a = g.x*.1;
    float e = sin(a+t)*(G-1.) + sin(g.x*.3+t)*1. + sin(a*5.3+t)*1.;
    if (e > g.y-G-G-G)
    {
        fragColor = vec4(1);
    }
    else
    {
        fragColor = vec4(0);
    }

    float xx = 64. - cos(iTime/2.)*32.;

    if (abs(g.x-xx) < 3. && abs(g.y-e-24.) < 1.5)
    {
        fragColor = vec4(.35,1,.35,0);
    }

    int xi = int(floor(fragCoord.x+3.)) & GMask;
    int yi = int(floor(fragCoord.y)) & GMask;
    if (xi < 2 || yi < 2)
    {
        fragColor = vec4(.3);
    }
    else if (xi >= GMask-1 || yi >= GMask-1)
    {
        fragColor = vec4(0);
    }
}
