void mainImage( out vec4 O, vec2 I)
{
    //Clear frag color
    O *= 0.;
    //Loop 9 times, rotating and scaling by 0.5
    for(vec2 c,i; i.x++<9.; I*=mat2(4,-3,3,4)*.1)
        //Pseudo random blocks (using last value as offset)
        O = sin(O+dot(c=ceil(I+O.r*.1),c)/.1);
    //Map to color range
    O *= cos(O/.1+vec4(6,2,3,3))*.5+.5*O;
}
