// This shader is part of several I wrote to create animations
// to support a presentation about Quantum Electrodynamics.
// it is not supposed to be "proof" for anything, but to provide 
// good visualization of some concepts to help intuition.


#define PI 3.1415926

#define sqr(x) (x * x)

vec2 rotate(vec2 p, float a)
{
    mat2 r = mat2(
        cos(a), sin(a),
        -sin(a), cos(a)
        );

    return p * r;
}

vec2 translate(vec2 p, vec2 newPos)
{
    return p - newPos;
}

float sdRect(vec2 p, vec2 size) 
{
    vec2 d = abs(p) - size / 2.0;
    return max(d.x, d.y);
}

float combineSdUnion(inout float sd1, float sd2)
{
    sd1 = min(sd1, sd2);
    return sd1;
}

vec4 drawSd(float d, inout vec4 prevCol, vec4 newCol, float aaTransitionWidth)
{
    prevCol = mix(prevCol, newCol, smoothstep(aaTransitionWidth, 0.0, d));
    return prevCol;
}

vec4 drawSd(float d, inout vec4 prevCol, vec4 newCol, float mask, float aaTransitionWidth)
{
    prevCol = mix(prevCol, newCol, clamp(mask, 0.0, 1.0) * smoothstep(aaTransitionWidth, 0.0, d));
    return prevCol;
}


float sdEllipse(vec2 p, vec2 r)
{
    return length(p * r.x / r) - r.x;
}

float sdOutline(float sd, float thickness)
{
    return abs(sd) - thickness * 0.5;
}

float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}

float sdVector(in vec2 p, in float len, in float thickness, in float appPointR, vec2 arrowHeadSize)
{
    vec2 arrowLineSize = vec2(thickness, len);
    float c = sdRect(p - vec2(0.0, (arrowLineSize.y - arrowHeadSize.y) / 2.0), arrowLineSize - vec2(0.0, arrowHeadSize.y));
    c = min(c, sdEllipse(p, vec2(appPointR)));
    c = min(c, sdTriangleIsosceles(p - vec2(0.0, arrowLineSize.y), vec2(arrowHeadSize.x, -arrowHeadSize.y)));

    return c;
}

float sdCosWave(vec2 p, float frequency, float amplitude, float initialPhase, float thickness)
{
    return min(abs(p.y - cos(p.x / frequency + initialPhase) * amplitude) - thickness * 0.5,
                abs(p.y - cos((p.x - thickness * 0.5) / frequency + initialPhase) * amplitude) - thickness * 0.5);
}

float sdSinWave(vec2 p, float frequency, float amplitude, float initialPhase, float thickness)
{
    return min( abs(p.y - sin(p.x / frequency + initialPhase) * amplitude) - thickness * 0.5,
                abs(p.y - sin((p.x - thickness * 0.5) / frequency + initialPhase) * amplitude) - thickness * 0.5);
}

float sd2SinWave(vec2 p, float frequency1, float frequency2, float amplitude1, float amplitude2, float initialPhase1, float initialPhase2, float phaseFix, float thickness)
{
    p.x += phaseFix;
    float f = sin(p.x / frequency1 + initialPhase1) * amplitude1 + 
              sin(p.x / frequency2 + initialPhase2) * amplitude2;
    return min(abs(p.y - f) - thickness * 0.5,
               abs(p.y - thickness - f) - thickness * 0.5);

}

float sdHLine(vec2 p, float thickness)
{
    return abs(p.y) - thickness * 0.5; 
}

float sdVLine(vec2 p, float thickness)
{
    return abs(p.x) - thickness * 0.5; 
}

vec4 hFade()
{
    return vec4(0);
}

void drawPhasorDiagram(vec2 p, inout vec4 color, float aaTransitionWidth, float frequency, float amplitude, float currentPhase, float thickness)
{
    vec2 phasorHOffs = vec2(-0.4, 0.0);

    float appPointR = thickness * 2.0;
    vec2 arrowHeadSize = thickness * vec2(4, 8);


    drawSd(sdOutline(sdEllipse(translate(p, phasorHOffs),  vec2(amplitude)), thickness), color, vec4(1.0), aaTransitionWidth);
    drawSd(sdVector(rotate(translate(p, phasorHOffs), currentPhase), amplitude, thickness, appPointR, arrowHeadSize), color, vec4(1.0, 1.0, 0.0, 1.0), aaTransitionWidth);    
}

void drawPhasorDiagram_(vec2 p, inout vec4 color, float aaTransitionWidth, float frequency, float amplitude, float currentPhase, float thickness)
{
    vec2 phasorHOffs = vec2(-0.4, 0.0);

    float appPointR = thickness * 2.0;
    vec2 arrowHeadSize = thickness * vec2(4, 8);


    drawSd(sdOutline(sdEllipse(translate(p, phasorHOffs),  vec2(amplitude)), thickness), color, vec4(1.0), aaTransitionWidth);
    drawSd(sdVector(rotate(translate(p, phasorHOffs), currentPhase), amplitude, thickness, appPointR, arrowHeadSize), color, vec4(1.0, 1.0, 0.0, 1.0), aaTransitionWidth);

    drawSd(sdHLine(translate(p, vec2(0.0, amplitude * cos(currentPhase))), thickness), color, vec4(1.0), 0.5, aaTransitionWidth);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
       vec2 uv = fragCoord.xy / iResolution.y;
    vec2 center = vec2(iResolution.x / iResolution.y / 2.0, 0.5);
    float aaTransitionWidth = 1.0 / (min(iResolution.x, iResolution.y)) * 2.0;

    float t = iTime * 0.1;

    float frequency1 = 10.5;
    float frequency2 = 80.0;
    float initPhase1 = 0.0;
    float initPhase2 = 0.0;
    float amplitude1 = 0.3;
    float amplitude2 = 0.1;
    float currentPhase1 = t * frequency1 + initPhase1;
    float currentPhase2 = t * frequency2 + initPhase2;

    float thickness = 0.003;

    // background gradient
    vec4 c = clamp(sqrt(mix(sqr(vec4(0.25, 0.2, 0.1, 1.0)), sqr(vec4(0.1, 0.22, 0.3, 1.0)), clamp(uv.y + uv.x / 2.0, 0.0, 1.0))), 0.0, 1.0);


    vec2 phasorHOffs = vec2(-0.5, 0.0);

    drawPhasorDiagram(translate(uv, center), c, aaTransitionWidth, frequency1, amplitude1, currentPhase1, thickness);

    drawPhasorDiagram_(translate(uv, center + amplitude1 * vec2(-sin(currentPhase1), cos(currentPhase1))), c, aaTransitionWidth, frequency2, amplitude2, currentPhase2, thickness);

    float pf2 = 2.983;
    float phaseFix = pf2 + 0.056;
    drawSd(sd2SinWave(translate(uv, vec2(0.0, 0.5)), 1.0 / frequency1, 1.0 / frequency2, amplitude1, amplitude2, currentPhase1, currentPhase2, phaseFix, thickness), c, vec4(1.0, 0.0, 1.0, 1.0), uv.x + 0.3, aaTransitionWidth * 2.0);


    drawSd(sdVLine(translate(uv, vec2(1.3, 0.0)), thickness), c, vec4(1.0), 0.5, aaTransitionWidth);

    fragColor = c;
}
