// cosine based palette. from Inigo Quilez
vec3 palette( in float t )
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(2.0, 1.0, 0.0);
    vec3 d = vec3(.5,.2,.25);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set scale; sizes to y resolution
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // base color
    vec3 col = palette(length(uv)/10.+iTime/7.);

    // find points a certain distance from circle w/ .5 radius
        // float d = length(uv);
        // d = sin(d * 8.0 + iTime) / 8.0;
        // d = abs(d);

    // make a spiral (angle from x-axis: atan(y/x))
    float tightness = 1.6;
    float speed = .6;
    float sufficientlyLargeFloat = 100000.;
    float numPins = 8.0; // keep even
    float thickness = 15.0;
    // makes distance from center, angle from x-axis,
    // and time factors
    float d = length(uv)
        + atan(uv.y / uv.x) / tightness
        + iTime * speed
        + sufficientlyLargeFloat;
    // d = abs(d);
    // makes it repeat
    d = sin(d*numPins)/thickness;
    d = abs(d);

    d = .015/d;

    col *= d;

    // Output to screen
    fragColor = vec4(col,1.0);
}
