vec2 rotate2D(vec2 uv, float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c) * uv;
}

vec2 hash12(float t) {
    float x = fract(sin(t * 3453.329));
    float y = fract(sin((t + x) * 8532.732));
    return vec2(x, y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    uv = rotate2D(uv, 3.14 / 2.0);

    vec3 col = vec3(0.0);

    float r = 0.17;

    for (float i = 0.0; i < 60.0; ++i)
    {
        float factor = (sin(iTime) * 0.5 + 0.5) + 0.3;
        i += factor;

        float a = i / 2.0;

        float dx = 2.0 * r * cos(a) - r * cos(2.0 * a);
        float dy = 2.0 * r * sin(a) - r * sin(2.0 * a);

        col += 0.013 / length(uv - vec2(dx + 0.1, dy) - 0.02 * hash12(i));
    }

    col *= sin(vec3(0.2, 0.8, 0.9) * iTime) * 0.15 + 0.25;

    fragColor = vec4(col,1.0);
}
