void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define M 1.0
#define HOOD_WIDTH 1.0 * M
#define HOOD_HEIGHT 1.0 * M
#define TREE_DENSITY 0.35
#define FIRE_DENSITY 0.0000000000000000000001

float rand(vec2 co){
  	return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * length(iDate));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cellWidth = 1.0/iResolution.xy;
   	vec2 uv = fragCoord.xy / iResolution.xy;

	//	sample yourself
    vec4 cell = texture(iChannel0, uv);

    //  are you a tree
    if(cell.g == 1.0){
        //  check if neighbors are on fire with diagonals
        for( float yi = -HOOD_HEIGHT; yi <= HOOD_HEIGHT; yi++ )
        {
            for( float xi = -HOOD_WIDTH; xi <= HOOD_WIDTH; xi++ )
            {
                vec2 xy = vec2(xi,yi) * cellWidth + uv;
                xy = vec2(mod(xy.x, 1.0), mod(xy.y, 1.0) );
                vec4 neighbor = texture(iChannel0, xy);
                if( neighbor.r == 1.0){
                    cell.g = 0.0;
                    cell.r = 1.0;
                }
            }
        }
    }  
    fragColor = cell;

    //  reset on click
    if (iMouse.z >=0.0){
        vec4 fire = min( vec4(1.0, 0.0, 0.0,1.0) * vec4(step(rand(uv),FIRE_DENSITY)), 1.0);        
        vec4 tree = min( vec4(0.0, 1.0, 0.0,1.0) * vec4(step(rand(uv),TREE_DENSITY)), 1.0);        
        fragColor = tree + fire;
    }
}
