float wave(float x) { return sin(-min(0., x)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) /iResolution.yy;

    if (abs(uv.x) > 1. || abs(uv.y) > 1.) { 
        fragColor = vec4(0.0);
        return;
    }

    float a = 0.0;

    for (int y = -3; y <= 3; y++)
    for (int x = -3; x <= 3; x++) {
        vec2 d = vec2(x, y);
        float f = wave(length(uv + 2. * d)*10. - iTime*2.5);
        f = smoothstep(0.8, 1.2, f);
        f *= exp(-0.8*dot(abs(d), vec2(1.)));
        a += f;//.5 * f +.5;
    }
    //a -= 20.;
    //a /= 40.;

    vec3 col = vec3(1.0) * mix(0.2, 1.0, a);

    // Output to screen
    fragColor = vec4(col,1.0);
}
