// pixel radius for smoothing out the normal 'peak'
#define smooth_out 40.0

// precision for alpha channel, change depending on color range
#define alpha_precision 25500.0


void mainImage(out vec4 col, in vec2 pos) {

    col = texture(iChannel0, pos / iResolution.xy);

    // if the left mouse button is pressed
    if (iMouse.z > 0.0) {

        // check the distance from the mouse position
        float o = 1.0 - length(iMouse.xy - pos) / alpha_precision;

        // if it's less distant than it was before
        // previous distance stored in alpha channel
        if (col.a < o) {

            // override the colors
            // and store the new distance in the alpha channel
            vec3 paint_col = vec3(0.5 + 0.5 * normalize(vec3(pos - iMouse.xy, smooth_out)));
            col = vec4(paint_col, o);

        }

    }

}
