mat2  M = mat2(6,8,-8,6)*.1;
float i,t,s,j,T,S;

float D(vec3 p)
{
	for(i=1e2, t = p.y+8.; i > 1.; i/=4. )
		p.zx *= M,
		p.y += i*.8,
		p = i*.8 - abs( mod(p,i+i) - i ),
		s = min(p.x,min(p.y,p.z)),
        t < s ? t=s, j=i : j;
	return t;
}

void mainImage(out vec4 O, vec2 I)
{
    vec3 r = iResolution, 
         d = vec3(I+I,0) - r.xyy, 
         p = 2. - iTime/r/r/.1,
         n = .01/r;

    for(d.yz *= M; r.z++ < 2e2; T += S )
        S = D(p),
        p += d/length(d)*S;

    O = 1. + cos(j*vec4(9,17,24,0));
    n = ( vec3( D(p+n.zxx), D(p+n.xzx), D(p+n)) - S ) / n.z;

    for(S=.01*T; S < .2*T; S/=.8 )
        O *= max( D(p+n*S)/S, .02 )
          *  max( D(p+  S)/S, .01 );
    O = pow(O/1e3, vec4(.04)) + T/6e2;
}

