#define Main void mainImage(out vec4 Q,vec2 U)
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

Main {
    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    Q.xy -= .5*vec2(e.z-w.z,n.z-s.z);
    Q.z = clamp(Q.z,-1.,1.);
    //if (length(U-vec2(.1,.5)*R)<20.)
    //    Q.xy *= 0.;
    vec4 c = C(U);
    if(length(U-c.xy)<c.w) 
        Q.xy *= 1.-.1*pow(c.z,10.);
    if (iFrame < 1||U.x<1.||R.x-U.x<1.||R.y-U.y<1.||U.y<1.)
        Q = vec4(.3,0,Q.z,.5+.5*sign(sin(.1*U.y)));
}

vec4 T (vec2 U) {
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    return A(U);
}
Main {
    Q = T(U);
    vec4 n = T(U+vec2(0,1));
    vec4 e = T(U+vec2(1,0));
    vec4 s = T(U-vec2(0,1));
    vec4 w = T(U-vec2(1,0));
    Q.z -= .5*(e.x-w.x+n.y-s.y);
}

Main {
    Q = C(U);
    for (float x = -1.; x <= 1.; x++)
    for (float y = -1.; y <= 1.; y++)
    {
        vec4 c = C(U+vec2(x,y));
        if (length(U-c.xy)<length(U-Q.xy))
            Q = c;
    }

    Q.xy += A(Q.xy).xy;
    if (iFrame < 1||U.x<1.) {
        float x = hash33(vec3(U,iFrame)).x;
        Q = vec4(floor(U/10.)*10.,x,5.*pow(x,10.));
      }
}
