float sussy(float x, float y){

    float a = max(abs(abs(x) - 4.), abs(y + sign(x)*.5));
    float b = max(abs(abs(x*2.) - 5.) - 2., abs(abs(y*2.) - 2.));
    float c = max(abs(abs(abs(x*2.) - 5.) - 2.), abs(y*2. + sign(x)*4.));

    return min(min(a,b),c) - 1.;
}

float impostor(float x, float y){

    float a = mod(x + 2.*round(y*.2 - round((x*5. + y*2.)/66.)*.6) - 6., 12.) - 6.;
    float b = mod(y - 3.*round((x*5. + y*2.)/66.) - 2.5, 5.) - 2.5;

    return -sussy(a, b);
}

vec3 among(vec2 z){

    z = vec2( 6.*log(z.x*z.x + z.y*z.y), 10.5*atan(z.y, z.x)) - vec2(8,2)*iTime;

    float blur = 0.; 

    for(float i=0.; i<.7; i+=.07){
        blur += step(0., impostor(z.x - i,z.y - .25*i));
        blur += step(0., impostor(z.x - i,z.y - .25*i - 11.))*.4;
    }

    return vec3(blur*.1, .0, .2);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 us = (2.*fragCoord - iResolution.xy)/iResolution.y*15.;

    fragColor = vec4(among(us) ,1.0);
}
