// Minimal implementation of Troy Sobotka's AgX based on
// 
// https://github.com/sobotka/AgX
//
// using a 6/7th order polynomial approximation instead
// of the more complex sigmoid function or lookup table.

// Set to > 0 to show a video instead of the color gradients
#define SHOW_VIDEO 0
// Set to > 0 for 7th instead of 6th order polynomial
#define QUALITY 0
// 0: Default, 1: Golden, 2: Punchy
#define LOOK 0

// AgX
// ->

#if QUALITY == 0
// Mean error^2: 3.6705141e-06
vec3 agxDefaultContrastApprox(vec3 x) {
  vec3 x2 = x * x;
  vec3 x4 = x2 * x2;

  return + 15.5     * x4 * x2
         - 40.14    * x4 * x
         + 31.96    * x4
         - 6.868    * x2 * x
         + 0.4298   * x2
         + 0.1191   * x
         - 0.00232;
}
#else
// Mean error^2: 1.85907662e-06
vec3 agxDefaultContrastApprox(vec3 x) {
  vec3 x2 = x * x;
  vec3 x4 = x2 * x2;

  return - 17.86     * x4 * x2 * x
         + 78.01     * x4 * x2
         - 126.7     * x4 * x
         + 92.06     * x4
         - 28.72     * x2 * x
         + 4.361     * x2
         - 0.1718    * x
         + 0.002857;
}
#endif

vec3 agx(vec3 val) {
  const mat3 agx_mat = mat3(
    0.842479062253094, 0.0423282422610123, 0.0423756549057051,
    0.0784335999999992,  0.878468636469772,  0.0784336,
    0.0792237451477643, 0.0791661274605434, 0.879142973793104);
  const float minEv = -12.47393f;
  const float maxEv = 4.026069f;

  // Input transform
  val = agx_mat * val;

  // Log2 space encoding
  val = clamp(log2(val), minEv, maxEv);
  val = (val - minEv) / (maxEv - minEv);

  // Apply sigmoid function approximation
  val = agxDefaultContrastApprox(val);

  return val;
}

vec3 inv_agx(vec3 val) {
  const mat3 inv_agx_mat = inverse(mat3(
    0.842479062253094, 0.0423282422610123, 0.0423756549057051,
    0.0784335999999992,  0.878468636469772,  0.0784336,
    0.0792237451477643, 0.0791661274605434, 0.879142973793104));

  // Input transform
  val = inv_agx_mat * val;

  return val;
}

vec3 agxLook(vec3 val) {
  const vec3 lw = vec3(0.2126, 0.7152, 0.0722);
  float luma = dot(val, lw);

  vec3 offset = vec3(0.0);

#if LOOK == 1
  // Golden
  vec3 slope = vec3(1.0, 0.9, 0.5);
  vec3 power = vec3(0.8);
  float sat = 0.8;
#elif LOOK == 2
  // Punchy
  vec3 slope = vec3(1.0);
  vec3 power = vec3(1.35, 1.35, 1.35);
  float sat = 1.4;
#else
  // Default
  vec3 slope = vec3(1.0);
  vec3 power = vec3(1.0);
  float sat = 1.0;
#endif

  // ASC CDL
  val = pow(val * slope + offset, power);
  return luma + sat * (val - luma);
}

// <-

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy;

  float h = floor(1.0+24.0*fragCoord.y/iResolution.y)/24.0 * 3.141592 * 2.;
  float L = floor(fragCoord.x*24.0/iResolution.y)/(24.0/iResolution.y)/iResolution.x - 0.4;
  vec3 color = cos(h + vec3(0.0,1.0,2.0)* 3.141592*2.0/3.0);
  float maxRGB = max(color.r, max(color.g, color.b));
  float minRGB = min(color.r, min(color.g, color.b));

  vec3 col = exp(15.0*L)*(color-minRGB)/(maxRGB-minRGB);

#if SHOW_VIDEO > 0
  col = textureLod(iChannel0, uv, 0.0).rgb;
  col = toLinear(vec4(col, 1.0)).rgb;
#endif

  // AgX
  // ->
  col = agx(col);
  col = agxLook(col);
  col = inv_agx(col);
  // <-

  // EOTF
  //val = pow(val, vec3(2.2));

  fragColor = vec4(col, 1.0);
}

vec4 fromLinear(vec4 linearRGB) {
  bvec4 cutoff = lessThan(linearRGB, vec4(0.0031308));
  vec4 higher = vec4(1.055)*pow(linearRGB, vec4(1.0/2.4)) - vec4(0.055);
  vec4 lower = linearRGB * vec4(12.92);

  return mix(higher, lower, cutoff);
}

vec4 toLinear(vec4 sRGB) {
  bvec4 cutoff = lessThan(sRGB, vec4(0.04045));
  vec4 higher = pow((sRGB + vec4(0.055))/vec4(1.055), vec4(2.4));
  vec4 lower = sRGB/vec4(12.92);

  return mix(higher, lower, cutoff);
}
