#define MAX_STEPS 256
#define MAX_DIST 5.
#define MASS 0.1

#define DELTA 0.001

#define AA 0

const float PI = acos(-1.0);

const vec3 blackHolePos = vec3(0.0, 0.0, 2.0);
const vec3 scatterCoeff = vec3(20.0);

float sdfSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdf(vec3 p) {
    float sphereSdf = sdfSphere(p - blackHolePos + vec3(0.06, 0.00, 0.06), 0.06);

    return sphereSdf;
}

vec3 getNormal(vec3 p) {
    vec2 delta = vec2(0.001, 0.0);

    float c = sdf(p);
    float dx = c - sdf(p + delta.xyy);
    float dy = c - sdf(p + delta.yxy);
    float dz = c - sdf(p + delta.yyx);

    return normalize(vec3(dx, dy, dz));
}

vec3 calculateBlackbody(float temp) {
    vec3 result = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++) {
        float f = 1.0 + 0.5 * i; 
        result[int(i)] += 100.0 * (f * f * f) / (exp(19E3 * f / temp) - 1.0);  // Planck law
    }

    return result;
}


vec4 accretionDisk(vec3 p, vec3 axis) {
    vec3 center = p - blackHolePos;

    vec2 uv = center.xz;
    float absHeight = abs(center.y);
    float dist = length(uv);

    float outer = MASS * 13.0;
    float inner = MASS * 2.0;

    if (dist > outer) {
        return vec4(0.0);
    }

    mat3 rotateMatrix = rotation3d(vec3(0.0, 1.0, 0.0), iTime + 10.0 * MASS / dist);

    vec3 noise = texture(iChannel1, rotateMatrix * center * 0.5).rgb;

    float heightTerm = exp(-absHeight * absHeight * 900.0 * (dist + 1.));
    float diskTerm = clamp((1.0 - smoothstep(inner, outer, dist)) * 50.0, 0.0, 1.0);

    /*
    float jet = clamp((1.0 - smoothstep(0.0, 0.05, dist - absHeight * 0.25)), 0.0, 1.0)
              * clamp(absHeight - MASS, 0.0, 1.0) * noise.x
              * clamp(exp(-(absHeight - MASS * 3.0) * 16.0), 0.0, 1.0);
    */

    float density = diskTerm * heightTerm * clamp((dist - inner) * 4., 0.0, 1.0) * noise.x ;//+ jet;
    vec3 color = calculateBlackbody(40.0 * (noise.x + 1.) / pow(max(dist - inner, 1e-6) * 0.1 + 0.05, 2.0));

    return vec4(color * 0.1, density);
}

float getGravitationalForce(float t) {
    return MASS / (t * t * t);
}

vec3 bendRay(vec3 ray, vec3 halfVec, float t, float stepSize) {
    float force = getGravitationalForce(t);
    return normalize(ray - force * stepSize * halfVec);
}

vec3 renderImage(vec2 fragCoord) {
    vec2 worldUv = (2.0 * fragCoord - iResolution.xy) / iResolution.x;

    vec3 rd = normalize(vec3(worldUv, 1.0));

    vec2 nMouse = iMouse.xy / iResolution.xy;

    if (int(iMouse.x + iMouse.y) < 4) {
        nMouse = vec2(0.2, 0.2);
    }

    vec3 ro = vec3(0.0, nMouse.y, nMouse.x * 4.);
    mat3 rotateM = rotation3d(normalize(vec3(0.0, 1.0, 1.0)), nMouse.x * 2.);
    rd = rotateM * rd;

    float t = 0.0;
    vec3 raypos = ro;
    vec3 bentRd = rd;

    bool bhHit = false;
    bool hit = false;

    float maxRayStep = MAX_DIST / float(MAX_STEPS);

    vec3 scattering = vec3(0.0);
    vec3 transmittance = vec3(1.0);

    for (int i = 0; i < MAX_STEPS; i++) {
        //float currsdf = sdf(raypos);

        vec3 halfVec = raypos - blackHolePos;
        float blackHoleT = length(halfVec);

        float heading = dot(bentRd, halfVec);

        float eh = blackHoleT - MASS * clamp(-heading / (abs(blackHoleT - MASS) + 1e-3), 0.0, 1.0);

        float closeSteps = 0.05 * eh * (eh + 2.0 * MASS) / MASS + 1e-3;
        float stepDist = min(maxRayStep, closeSteps);

        bentRd = bendRay(bentRd, halfVec, blackHoleT, stepDist);

        vec4 disk = accretionDisk(raypos, vec3(0.0, 1.0, 0.0));
        float density = disk.a * stepDist;

        vec3 stepTransmittance = exp(-density * scatterCoeff);

        scattering += (1.0 - stepTransmittance) * disk.rgb * transmittance;
        transmittance *= exp(-density * scatterCoeff);

        if (blackHoleT <= MASS && heading < 0.0) {
            bhHit = true;
            break;
        }

        //if (currsdf < DELTA) {
        //    hit = true;
        //    break;
        //}

        raypos += bentRd * stepDist;
    }

    //vec3 cubemap = texture(iChannel0, bentRd).rgb;
    //cubemap = cubemap / (1.0 - cubemap + 1e-6);
    vec3 cubemap = vec3(0.0);

    vec3 color = bhHit ? vec3(0.0) : cubemap;//* transmittance + scattering / PI;

    //if (hit) {
    //    vec3 normal = getNormal(raypos);
    //    color = vec3(1.);
    //}

    color = color * transmittance + scattering; 

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 color = vec3(0.0);
        if (AA > 0) {
        for (int i = -AA; i <= AA; i++) {
            for (int j = -AA; j <= AA; j++) {
                    vec2 offsetCoord = fragCoord + vec2(i, j) * 0.5 / float(AA);
                    color += renderImage(offsetCoord);
               }
        }

        int totalSteps = 2 * AA + 1;

        color /= float(totalSteps * totalSteps);
    } else {
        color = renderImage(fragCoord);
    }

    color /= color + 1.0;

    fragColor = vec4(pow(color, vec3(1.0 / 2.2)), 1.0);
}

mat3 rotation3d(vec3 axis, float angle) {
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(
        oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
        oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
        oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c
    );
}

float bayer2(vec2 a){
    a = floor(a);
    return fract( dot(a, vec2(.5, a.y * .75)) );
}

#define bayer4(a)   (bayer2( .5*(a))*.25+bayer2(a))
#define bayer8(a)   (bayer4( .5*(a))*.25+bayer2(a))
#define bayer16(a)  (bayer8( .5*(a))*.25+bayer2(a))
