void mainImage( out vec4 O, vec2 u )
{
    O = texture(iChannel0, u/R);               // show threshold (converging toward median )
    if ( u.x/R.x > .75 )
        O = texture(iChannel1, u/R );          // show local threshold mask
    else if ( u.x/R.x > .5 )
        O = textureLod(iChannel1, u/R, W );    // show windowed counting
}

// === adjust the value threshold towards the median

void mainImage( out vec4 O, vec2 u )
{
    O = textureLod(iChannel1, u/R, W); // proportion of texture below threshold within window 2^W

    O = texture(iChannel0, u/R) * (1. + .1*(.5-O) ); // adjust threshold via relaxation

    if (iFrame<1 || iMouse.w>0. ) O = vec4(.5);       // init
}

// === prepare the count of texture pixels below the buffA threshold 

void mainImage( out vec4 O, vec2 u )
{
    O = step(  texture(iChannel1, u/R),
               texture(iChannel0, u/R)
            // textureLod(iChannel0, u/R, W)
            );
}

//float W = 3.;
#define W ( iMouse.z>0. ? 8.*iMouse.y/R.y : log2(R.y/45.) )

#define R iResolution.xy
