void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Time
    float t = iTime * 0.5;

    // UV coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Center the UV coordinates
    uv -= 0.5;

    // Apply rotation to the UV coordinates
    float rotation = 45.0 * t; // Change this value to adjust rotation angle
    float cosR = cos(radians(rotation));
    float sinR = sin(radians(rotation));
    mat2 rotMat = mat2(cosR, -sinR, sinR, cosR);
    uv = rotMat * uv;

    // Scroll speed and direction
    float scrollSpeed = 0.2;
    vec2 scrollDir = vec2(1.0, 1.0);

    // Tile size
    vec2 tileSize = vec2(0.25, 0.25);

    // Offset the UV coordinates based on time and scroll speed
    uv += scrollDir * t * scrollSpeed;

    // Wrap the UV coordinates to create seamless scrolling
    uv = mod(uv, tileSize) / tileSize;

    // Flip the image on the x axis for every new tile
    if (floor(uv.y / tileSize.y) == 1.0) {
        uv.x = 1.0 - uv.x;
    }

    // Flip the image on the y axis for every other vertical tile
    if (floor(uv.x / tileSize.x) == 1.0) {
        if (mod(floor(uv.y / tileSize.y), 2.0) == 1.0) {
            uv.y = 1.0 - uv.y;
        }
    }

    // Output the final color
    fragColor = texture(iChannel0, uv);
}
