void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 P, Q;
    float i,  d = 1.0;

    for (; i < 99.0 && d > 0.0001; ++i) {
        P += normalize(vec3(fragCoord , iResolution.x)) * d;
        Q = P;

        for (int j = 0; j < 9; ++j) {
         Q = abs(Q) - sin(iTime)*0.7-0.2;
         Q = Q.y > Q.x ? Q.yxz : Q;
         Q = Q.z > Q.y ? Q.xzy : Q;
         Q.z = abs(Q.z);
         Q.x = abs(Q.x);
          Q = (Q - 1.0)*3.0;
        }
        d = length(Q) / 4e4;
    }
    fragColor = vec4(vec3(9.0 / (i + 1.0)), 1.0);
}
