float Circle(vec2 uv, float r, vec2 p, float blur) {
    float d = length(uv - p);
    // Output to screen
    float c = 1.-smoothstep(r-blur,r,d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float c = Circle(uv, 0.4,vec2(0.0,0.0), 0.05);
    float planet = Circle(uv, 0.1,vec2(.85*cos(iTime),0.0), 0.05);
    c-=planet*sin(iTime);
    vec3 finalColor = smoothstep(0.0, .85, vec3(c,pow(c*.95,1.5),pow(c*.95,5.)));
    fragColor = vec4(finalColor,1.0);
}
