const int samples = 300;
const int zoomSamp = 1;

vec4 dirBlur(sampler2D tex, vec2 uv, vec2 angle)
{
    vec3 acc = vec3(0);
    vec3 accZoom= vec3(0);

    vec2 mainUV = uv;
    float alpha = 0.5;

    float time = iTime;

    // Define the transition duration in seconds
    float transitionDuration = 4.0; // Adjust this value as needed

    // Calculate the smoothstep value that varies with time and moves towards 0
    float smoothValue = smoothstep(0.5, -0.5, clamp(sin(time * 8.0 * exp( 1.4 * iTime) / transitionDuration), 0.0, 1.0)) + 0.5;


    vec2 frontUV = (uv - 0.5) * smoothValue + 0.5;

    const float delta = 5.0 / float(samples);

    for(float i = -1.0; i <= smoothstep(7.0, 0.0, iTime * 10.0); i += delta)
    {
        acc += texture(tex, uv - vec2(angle.x * i, angle.y * i)).rgb;
    }

    const float zoomDelta = 2.0 / float(zoomSamp);

    for(float i = -1.0; i <= 1.0; i += zoomDelta)
    {
        acc += texture(tex, uv - vec2(angle.x  * i, angle.y * i)).rgb;
    }


    vec4 mainColor = texture(iChannel0, mainUV);
    // Time varying pixel color
    vec4 frontColor = texture(iChannel1, frontUV);

    if(iTime >= 0.08)
    {
        vec4 finalColor = mix(vec4(delta * acc, 0), frontColor, smoothstep(0.5, 0.0, iTime *0.9));

        return vec4(finalColor);
    }
    else
    {
        return vec4(delta * acc, 0);
    }


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Calculate the UV coordinates for the front texture and zoom it in


    float angle = 0.0;
    float strength = smoothstep(1.0, 0.0, iTime * 9.8);;

    float r = radians(angle);
    vec2 direction = vec2(sin(r), cos(r));


    // Output to screen
    fragColor = dirBlur(iChannel0, uv, strength*direction);

}
