#define C(t) ( vec3(1.5,.6,.6) + vec3(1.25,.38,-2.5) * cos(6.28318*(vec3(-3.142,-3.223,2)*(t)+vec3(.31,-1.42,1.75)))) //
#define CS(a) vec2(cos(a),sin(a))


void mainImage( out vec4 O, in vec2 u )
{
    vec2 W = iResolution.xy, 
         U = (u - .5 * W) / W.y;

    vec3 col = vec3(0.0);

    for(float r = .05,m = 2.,R = .1,i,f,a ; i < 1e2; i++ )  {

        i+= f = sin(iTime/.5)*.5 + .8;
        a = i/5.;

        col += pow(.002/ length( U - f*(R + m*r) * CS(a) + m*R * CS(f*a + m*a) ) , 1.5) 
               * C( length(U) + i + iTime*.6 ); //циклоидальная кривая

        U*=mat2(cos(iTime*.1+vec4(0,11,33,0)));
    }

    O = vec4(col, 1.0);
}
