const float pi = acos(-1.0);

float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3  col=((sdCircle(p,0.1))>0.0)?vec3(0.0):vec3(1.0,0.0,0.0);
    float AA=3.0/min(iResolution.x, iResolution.y);

    float index=12.0;
    float time=fract(iTime*0.5);
    for(float petal=1.0;petal<=3.0;petal++)
    {
        float d=1.0;   
        for(float i=0.0;i<=index;i++)
        {
            float angle=radians(i*360.0/index);
            //angle*=iTime;
            vec2 t_p=vec2(cos(angle),sin(angle))*petal*0.5;
            t_p*=time;
            col=(max(d,sdCircle(p+t_p,index*0.01))>0.0)?col:vec3(1.0,fract(abs(max(p.x,p.y))),0.0);
            d=sdCircle(p+t_p,index*0.02);
        }
    }
	fragColor = vec4(col,1.0);
}
