#define PI     3.14159265

float hash12(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * uvec2(1597334673U, 3812015801U);
	uint n = (q.x ^ q.y) * 1597334673U;
	return float(n) * (1.0 / float(0xffffffffU));
}

// Noise function by morgan3d
float perlinNoise(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);

	float a = hash12(i);
    float b = hash12(i + vec2(1.0, 0.0));
    float c = hash12(i + vec2(0.0, 1.0));
    float d = hash12(i + vec2(1.0, 1.0));
    vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xx;

    vec2 circle = vec2(iTime * 0.1, sin(iTime * .2) * 0.4) * 50.;
    float noise = perlinNoise(uv * 30. * (1.8 + sin(iTime * 0.3) * .4) + circle); 
    uv *= sin(iTime * 0.5) * 5.;
    noise += uv.y - uv.x;
    uv += vec2(noise * 30., noise * 3.);

    float x = length(uv - round(uv)); 
    float dots = smoothstep(0., 0.6 + sin(iTime * .11) * .1, x);

    vec3 col = dots * vec3(
        noise + cos(iTime * .3 + uv.x),
        (1.0 + sin(iTime + PI * 2. / 3.) / 2.) * noise,
        noise + sin(iTime + PI * 4. / 3.) / 2.
    );

    fragColor = vec4(col,1.0);
}
