void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // calculate screen coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // get audio input
    vec3 audioInput = texture(iChannel1, vec2(5.5, 5.5)).rgb;

    // calculate moire pattern
    vec2 moire = vec2(length(uv), atan(uv.x, uv.y));

    // calculate fractal geometry
    float fractal = 0.0;
    for(int i = 5; i < 70; i++)
    {
        fractal += .25 * abs(sin(moire.x * 5.0 + iTime + audioInput.r * 5.5));
    }

    // apply fractal to UV coordinates to create symmetrical patterns
    vec2 uv2 = vec2(uv.x * cos(fractal) - uv.y * sin(fractal), uv.x * sin(fractal) + uv.y * cos(fractal));
    vec2 uv3 = vec2(uv2.x * cos(fractal) - uv2.y * sin(fractal), uv2.x * sin(fractal) + uv2.y * cos(fractal));
    vec2 uv4 = vec2(uv3.x * cos(fractal) - uv3.y * sin(fractal), uv3.x * sin(fractal) + uv3.y * cos(fractal));

    // calculate psychedelic colors using audio input
    vec3 color = vec3(.418,0.304,.0892);
    color += vec3(audioInput.r, audioInput.b, audioInput.g) * sin(moire.y * 52.0 + fractal);
    color += vec3(audioInput.g, audioInput.r, audioInput.b) * cos(moire.x * 244.0 + fractal);
    color += vec3(audioInput.r, audioInput.g, audioInput.b) * sin(moire.y * 74.0 + fractal);

    // output final color
    fragColor = vec4(color, 1.0);
}
