vec2 fragCoord_to_uv(in vec3 uniformResolution, in vec2 fragCoord) {
    vec2 uv = fragCoord - uniformResolution.xy/2.0;
    return uv*2.0/uniformResolution.x;
}

vec2 mouse_to_fragCoord(vec4 mouse) {
    return mouse.xy + 0.5;
}

vec2 mouse_to_uv(in vec3 uniformResolution, vec4 mouse) {
    return fragCoord_to_uv(uniformResolution, mouse_to_fragCoord(mouse));
}

