#define BACKGROUND vec4(0.2509, 0.1137, 0.0118, 0.0)
#define BRASS      vec4(0.4667, 0.3333, 0.0510, 1.0)
#define BLACK      vec4(0.0,    0.0,    0.0,    1.0)

#define BRASS_HEIGHT     0.20
#define BRASS_WIDTH      0.75
#define BORDER_THICKNESS 0.02
#define ROUNDING_RADIUS  0.10


void mainImage( out vec4 fragColor, vec2 uv )
{
    fragColor = vec4(0.0);
    uv -= iResolution.xy/2.0;
    uv /= iResolution.x;
    uv *= 2.0;

    fragColor = BACKGROUND;
    vec2 abs_uv = abs(uv);
    vec2 corner_pos = vec2(BRASS_WIDTH, BRASS_HEIGHT);
    if(abs_uv.x < BRASS_WIDTH && abs_uv.y < BRASS_HEIGHT && length(abs_uv - corner_pos) > ROUNDING_RADIUS) {
        fragColor = BRASS;
    } else if (
            abs_uv.x < BRASS_WIDTH + BORDER_THICKNESS && 
            abs_uv.y < BRASS_HEIGHT + BORDER_THICKNESS &&
            length(abs_uv - corner_pos) > ROUNDING_RADIUS - BORDER_THICKNESS) {
        fragColor = BLACK;
    }


    float num_characters = 9.0;
    float FontSize = 18.;
    float width = (num_characters + 1.0)*FontSize/256.0;
    float height = FontSize/128.0;

    vec2 position = vec2(-width, -height);
    vec2 U = ( uv - position)*64.0/FontSize;
    C(-32);C(6);C(15);C(15);C(4);C(9);C(14);C(9);C(-32);  
    fragColor = clamp(fragColor, 0.0, 1.0);
}


