const float PI = 3.1415926538;
const float EULER = 2.71828183;
int rand_seed = 2987492621;
// THIS SUCKS and is making antialiasing terrible.
float rand(in vec2 co){
    int chaos = int(co.x * 867530.9 * sin(PI + iTime) + co.y * 555236.8 * cos(EULER + iTime));
    rand_seed += chaos;
    rand_seed *= 1664525;
    rand_seed += 1013904223;
    return abs(float(rand_seed) / float(2147483647));
}

vec3 rand_normalized_vector() {
    /*
    You can generate normally-distributed numbers on the surface of the unit sphere with something like
    the following. The math should be right, but I've not tried it. Note the cost. 
    */
    float phi = 2.0 * PI * rand(vec2(EULER, PI));
    float theta = acos(2.0 * rand(vec2(EULER, PI)) - 1.0);
    float u = cos(phi);
    float root = sqrt(1.0 - u*u);
    float x = root * cos(theta);
    float y = root * sin(theta);
    float z = u;


    return vec3(x, y, z);
}

bool approximately_equal(float a, float b, float epsilon) {
    return abs(a-b) < epsilon;
}

