float sphere_dist(in Object sphere, in Ray ray) {
    vec3 OC = sphere.sphere_pos - ray.origin;
    float len_OC_squared = dot(OC, OC);
    float r_squared = sphere.sphere_radius*sphere.sphere_radius;
    float Tca;
    float Tca_squared;
    float Thc_squared;

    Tca = dot(OC, ray.dir);

    // Is the ray origin outside the sphere?
    if(len_OC_squared >= r_squared) {
        // Does the ray point away from the sphere?
        if(Tca < 0.0) {
            return MAX_DIST;
        } else {
            Tca_squared = Tca * Tca;
            Thc_squared = r_squared - len_OC_squared + Tca_squared;
        }
        // Does the ray miss the sphere?
        if(Thc_squared < 0.0)
            return MAX_DIST;
        return Tca - sqrt(Thc_squared);
    } else {
        Tca_squared = Tca * Tca;
        Thc_squared = r_squared - len_OC_squared + Tca_squared;
        return Tca + sqrt(Thc_squared);
    }
}

float plane_dist(in Object plane, in Ray ray) {
    float Vprd = dot(plane.plane_normal, ray.dir);
    float Vpro = -(dot(plane.plane_normal, ray.origin) + plane.plane_displacement);
    float t = Vpro / Vprd;
    if(t>0.0)
        return t;
    return MAX_DIST;
}

float _cylinder_dist(in vec3 cap0_pos, in vec3 cap1_pos, in float radius, in Ray ray) {
    vec3 ba = cap1_pos - cap0_pos;
    vec3 oc = ray.origin - cap0_pos;

    float baba = dot(ba, ba);
    float bard = dot(ba, ray.dir);
    float baoc = dot(ba, oc);
    float k2 = baba - bard*bard;
    float k1 = baba * dot(oc, ray.dir) - baoc*bard;
    float k0 = baba * dot(oc, oc) - 
        baoc*baoc - radius * radius * baba;

    float h = k1*k1 - k2*k0;
    if( h<0.0 ) 
        return MAX_DIST;

    h = sqrt(h);
    float t = (-k1-h)/k2;

    // body
    float y = baoc + t * bard;
    if(y > 0.0 && y < baba) {
        return t > 0.0 ? t : MAX_DIST;
    }

    // caps
    t = ( ((y<0.0) ? 0.0 : baba) - baoc)/bard;
    if( abs(k1+k2*t)<h )
    {
        return t > 0.0 ? t : MAX_DIST;
    }

    return MAX_DIST;
}

// I don't have a cylinder intersector that works from inside the cylinder - it only finds the near faces.
// To deal with this, I back up the starting ray by the smallest amount that is guaranteed to take an
// interior start point to the exterior (the sum of the height plus the diameter.) If an intersection is
// found, and it is found to be nearer than this bias, I do another test from the other side of the 
// cylinder.... which is guaranteed to hit. Whichever point is in front of - and nearest to - the original
// ray is our solution.
float cylinder_dist(in Object cylinder, in Ray ray) {    
    if(textures[cylinder.texture_index].texture_type != TEX_REFRACT) {
        return _cylinder_dist(cylinder.cylinder_cap0_pos, cylinder.cylinder_cap1_pos, cylinder.cylinder_radius, ray);
    } else {
        //TODO: Optimize this. Store the displacement distance in the object.
        Ray tmp_ray;
        tmp_ray.dir = ray.dir;
        tmp_ray.origin = ray.origin - cylinder.cylinder_height_plus_diameter * ray.dir;
        float first_pass_dist = _cylinder_dist(
            cylinder.cylinder_cap0_pos, cylinder.cylinder_cap1_pos, cylinder.cylinder_radius, tmp_ray);
        if(first_pass_dist == MAX_DIST)
            return MAX_DIST;
        if(first_pass_dist > cylinder.cylinder_height_plus_diameter)
            return first_pass_dist - cylinder.cylinder_height_plus_diameter;

        tmp_ray.origin = ray.origin + cylinder.cylinder_height_plus_diameter * ray.dir;
        tmp_ray.dir = -ray.dir;
        float second_pass_dist = _cylinder_dist(
            cylinder.cylinder_cap0_pos, cylinder.cylinder_cap1_pos, cylinder.cylinder_radius, tmp_ray);
        second_pass_dist = cylinder.cylinder_height_plus_diameter - second_pass_dist;
        return second_pass_dist;
    }
}

//#define DRAW_NORMALS
// TODO(Optimization): Do a version that skips anything that isn't necessary for refraction.
float tube_dist(in Object tube, in Ray ray) {
    // We're using a cylinder intersection routine that only gives us near hits, and we need 
    // a test that will work from inside geometry. So, we have to make this thing work - somewhat
    // optimally, for a ray that starts anywhere.

    Ray frontface_ray;
    frontface_ray.dir = ray.dir;
    frontface_ray.origin = ray.origin - tube.tube_height_plus_diameter * ray.dir;

    float major_frontface_dist;
    float minor_frontface_dist;
    float major_backface_dist;
    float minor_backface_dist;

    // Copy the initial ray but back it up by enough that, if the original ray started inside the cylinder, it is
    // now guaranteed to be outside the cylinder. If it misses, we have missed the entire thing.

    //MAJOR FRONTFACE:
    major_frontface_dist = _cylinder_dist(tube.tube_cap0_pos, tube.tube_cap1_pos, tube.tube_major_radius, frontface_ray);
    if(major_frontface_dist == MAX_DIST) {
        return MAX_DIST;
    }
    // remove the displacement bias. major_frontface_dist will now be negative if the ray started inside the cyl.
    // That's okay.
    major_frontface_dist -= tube.tube_height_plus_diameter;

    Ray backface_ray;
    backface_ray.dir = -ray.dir;
    float backface_ray_displacement = tube.tube_height_plus_diameter + major_frontface_dist;
    backface_ray.origin = ray.origin + backface_ray_displacement * ray.dir;

    // I could save a bit by doing these only when called for, but it wouldn't save much (the savings would be in the rare
    // cases) and this thing is 
    // MAJOR BACKFRACE
    major_backface_dist = _cylinder_dist(tube.tube_cap0_pos, tube.tube_cap1_pos, tube.tube_major_radius, backface_ray);
    if(major_backface_dist != MAX_DIST)
        major_backface_dist = backface_ray_displacement - major_backface_dist;

    // If the entire object is behind the ray:
    if(major_backface_dist < 0.0)
        return MAX_DIST;

    // MINOR FRONTFACE:
    minor_frontface_dist = _cylinder_dist(tube.tube_cap0_pos, tube.tube_cap1_pos, tube.tube_minor_radius, frontface_ray);
    if(minor_frontface_dist != MAX_DIST)
        minor_frontface_dist -= tube.tube_height_plus_diameter;
    // MINOR BACKFRACE
    minor_backface_dist = _cylinder_dist(tube.tube_cap0_pos, tube.tube_cap1_pos, tube.tube_minor_radius, backface_ray);
    if(minor_backface_dist != MAX_DIST)
        minor_backface_dist = backface_ray_displacement - minor_backface_dist;


    // All of the m*or_*face_dists are now the distance from the original ray to the surface they're testing. Negatives
    // represent things behind the source ray and MAX_DIST represents, of course, misses.

    bool frontfaces_equal = approximately_equal(major_frontface_dist, minor_frontface_dist, 0.001);
    if(!frontfaces_equal) {
        if(major_frontface_dist > 0.0) {
            return major_frontface_dist;
        }
        if(minor_frontface_dist > 0.0) {  // UNTESTED REFRACTION CASE
            return minor_frontface_dist;
        }
    }

    bool backfaces_equal = approximately_equal(major_backface_dist, minor_backface_dist, 0.001);
    if(!backfaces_equal) {
        if(minor_backface_dist > 0.0) {
            return minor_backface_dist;
        }
        if(major_backface_dist > 0.0) { // OPTIMIZE. This should always be true if we've gotten here.
            return major_backface_dist;
        }
    }

    return MAX_DIST;
}

// TODO: OPTIMIZE. dist_from_centerline computes nearest_point_on_centerline, which could be used again
//                 by dist_along_centerline. The former is used in get_normal as a preamble to the
//                 lighting calculation. The latter, by texturing.
float dist_from_centerline(in vec3 intersect_point, in vec3 cap0, in vec3 centerline, out vec3 centerline_to_intersect_point) {
    vec3 nearest_point_on_centerline = dot(intersect_point - cap0, centerline) * centerline + cap0;
    centerline_to_intersect_point = intersect_point - nearest_point_on_centerline;
    return length(centerline_to_intersect_point);
}

float fract_along_centerline(vec3 intersect_point, in vec3 cap0, in float height, in vec3 centerline) {
    vec3 nearest_point_on_centerline = dot(intersect_point - cap0, centerline) * centerline + cap0;
    return length(nearest_point_on_centerline - cap0) / height;
}

float plane_usdf(vec3 p, vec3 normal, float displacement) {
    return dot(normal, p) - displacement;
}

#define sdf_normal(normal,obj,p,mapping_func)\
    do {\
        vec2 e = vec2(1.0, -1.0) * 0.5773;\
        normal = normalize(\
            e.xyy*mapping_func(obj, p + e.xyy*0.0005) +\
            e.yyx*mapping_func(obj, p + e.yyx*0.0005) +\
            e.yxy*mapping_func(obj, p + e.yxy*0.0005) +\
            e.xxx*mapping_func(obj, p + e.xxx*0.0005));\
    } while(false)

float obj_dist(in Object obj, in Ray ray) {
    if(obj.type == SPHERE_TYPE) {
        return sphere_dist(obj, ray);
    } 
    if(obj.type == PLANE_TYPE) {
        return plane_dist(obj, ray);     
    }
    if(obj.type == CYLINDER_TYPE) {
        return cylinder_dist(obj, ray);
    }
    if(obj.type == TUBE_TYPE) {
        return tube_dist(obj, ray);
    }
}

// Returns a normal that points toward the ray source. Reflection, refraction, and lighting all
// depend upon this assumption. Refraction requires that we know if we're entering or exiting
// the object, so we return that information as well.
// TODO: save the dot product by taking a bool that tells the function whether or not it's needed.
vec3 get_normal(in Object object, in vec3 ray_dir, in vec3 intersect_point, out bool incident_upon_exterior) {
    vec3 normal;

    if(object.type == SPHERE_TYPE) {
        normal = normalize(intersect_point - object.sphere_pos);
    } else if(object.type == PLANE_TYPE) {
        normal = object.plane_normal;
    } else if(object.type == CYLINDER_TYPE) {
        float dot_prod = dot(object.cylinder_centerline, intersect_point - object.cylinder_cap0_pos);
        vec3 nearest_point_on_axis = 
            object.cylinder_cap0_pos + object.cylinder_centerline * dot_prod;
        vec3 direction = intersect_point - nearest_point_on_axis;
        float dist_from_nearest_to_intersect = length(direction);
        if(dist_from_nearest_to_intersect < (0.9999 * object.cylinder_radius)) {
            if(dot_prod < 0.01) {
                normal = -object.cylinder_centerline;
            } else {
                normal = object.cylinder_centerline;
            }
        } else {
            normal = normalize(intersect_point - nearest_point_on_axis);
        }
    } else if (object.type == TUBE_TYPE) {
        float r = dist_from_centerline(intersect_point, object.tube_cap0_pos, object.tube_centerline, normal);
        if(approximately_equal(r, object.tube_major_radius, 0.001)) {
            normal = normal/r;
        } else if(approximately_equal(r, object.tube_minor_radius, 0.001)) {
            normal = normal/r;
        } else if(approximately_equal(length(intersect_point - object.tube_cap0_pos), r, 0.001)) {
            normal = -object.tube_centerline;
        } else {
            normal = object.tube_centerline;
        }
    }
    incident_upon_exterior = (dot(ray_dir, normal) < 0.0);
    if(!incident_upon_exterior)
        normal = -normal;

    return normal;
}

// TODO: add the actual intersect point (since p is essentially a uv) and the normal
vec4 mand(in vec2 uv, in vec3 intersect_point, in vec3 normal) {
    vec2 c = vec2(uv.x, uv.y) * 0.003;// * (0.0005 + pow(sin(iTime/15.0), 2.0));
    c += vec2(0.1382309, 0.643002);
    vec2 z = c;
    vec2 zn;

    for(float i=0.; i<500.; i++) {
        if(dot(zn,zn) >= 4.0) {
            float d = i + 1. - log(log(length(zn)))/log(2.);
            return vec4(
                (1.0+sin((iTime*5. + d)/17.))/2.0,
                (1.0+cos((iTime*7. + d)/13.))/2.0,
                (1.0+sin((iTime*11. + d)/11.))/2.0,
                1.0);
        } else {
            zn = mat2(zn,-zn.y,zn.x) * zn + c;
    		z = zn;
        }
    }
    return vec4(0.0, 0.0, 0.0, 1.0);
}

vec4 grid(in vec2 p) {
    float sx = sin(p.x*PI);
    float sy = sin(p.y*PI);

    sx *= sx * sx;
    sx = pow(sx, 20.0);
    sy *= sy * sy;
    sy = pow(sy, 20.0);

    float intensity = max(sx, sy);
    intensity = max(0.0, intensity);
    intensity = min(1.0, intensity);
    return vec4(intensity, intensity, intensity, 1.0);
}

