    vec3 p1 = p + perlin(p).xyz * 0.4;
    return mand(p1.xz, intersect_point, normal);
}

struct RayContext {
    Ray ray;
    vec3 contribution;
};
const int max_contexts = 10;
int num_contexts = 0;
RayContext contexts[max_contexts];

bool push(in Ray ray, in vec3 contribution) {
    if(num_contexts == max_contexts)
        return false;

    contexts[num_contexts].ray = ray;
    contexts[num_contexts].contribution = contribution;
    num_contexts++;

    return true;
}

bool pop(out Ray ray, out vec3 contribution) {
    if(num_contexts == 0)
        return false;

    num_contexts--;
    ray = contexts[num_contexts].ray;
    contribution = contexts[num_contexts].contribution;

    return true;
}

float max_vec3(in vec3 i) {
    return max(max(i.x, i.y), i.z);
}

// Fresnel is very costly to compute. I should just replace this with a cheap approximation.
// https://www.scratchapixel.com/lessons/3d-basic-rendering/introduction-to-shading/reflection-refraction-fresnel.html
float fresnel_reflection_fraction(in vec3 ray_dir, in vec3 normal, in float ior) {
    //return false;

    float cosi = dot(ray_dir, normal);
    float etai;
    float etat;
    if (cosi < 0.0) {
        etai = 1.0;
        etat = ior;
    } else {
        etai = ior;
        etai = 1.0;
    }

    float sint = etai / etat * sqrt(max(0.0, 1.0 - cosi * cosi));
    if(sint >= 1.0)
        return 1.0;

    float cost = sqrt(max(0.0, 1.0 - sint * sint));
    cosi = abs(cosi);
    float Rs = ((etat * cosi) - (etai * cost)) / ((etat * cosi) + (etai * cost));
    float Rp = ((etai * cosi) - (etat * cost)) / ((etai * cosi) + (etat * cost));

    return (Rs * Rs + Rp * Rp) / 2.0;
}

void push_reflection_ray(in Ray ray, in vec3 normal, in vec3 intersect_point, in vec3 contribution) {
    if(max_vec3(contribution) > 0.005) {
        Ray new_ray;
        new_ray.dir = reflect(ray.dir, normal); // _SHOULDN'T_ need normalizing.....
        new_ray.origin = intersect_point + normal * 0.001;
        push(new_ray, contribution);
    }
}

void push_refraction_ray(in Ray ray, in vec3 normal, in vec3 intersect_point, in vec3 contribution, in float ior) {
    float eta = 1.0/ior;
    if(max_vec3(contribution) > 0.005) {
        Ray new_ray;
        new_ray.dir = refract(ray.dir, normal, eta); // _SHOULDN'T_ need normalizing.....
        if(dot(new_ray.dir, new_ray.dir) == 0.0) {
            // This is the "total internal reflection" case. glsl reflect() returns a zero vector for
            // this situation, so we have to make it ourselves:
            push_reflection_ray(ray, normal, intersect_point, contribution);
        } else {
            new_ray.origin = intersect_point - normal * 0.001;
            push(new_ray, contribution);
        }
    }
}

void push_transmissive_ray(Ray ray, in vec3 intersect_point, in vec3 contribution) {
    Ray new_ray = ray;
    new_ray.origin = intersect_point + ray.dir * 0.0005;
    push(new_ray, contribution);
}

vec4 channel_uv_lookup(in int channel_id, in vec2 uv) {
    if(uv.x < 0.0 || uv.x > 1.0 || uv.y < 0.0 || uv.y > 1.0) {
        return vec4(0.0, 0.0, 0.0, 0.0);
    }


    if(channel_id < 2) {
        if(channel_id == 0) {
            return texture(iChannel0, uv); 
        } else {
            return texture(iChannel1, uv); 
        }
    } else {
        if(channel_id == 2) {
            return texture(iChannel2, uv); 
        } else {
            return texture(iChannel3, uv); 
        }
    }
}

const float chroma_threshold = 0.585;
const float chroma_padding = 0.05;
float get_chromakey_opacity(in vec4 input_color) {
    vec3 background_color = vec3(0.0, 1.0, 0.0);
    vec3 diff = input_color.rgb - background_color;
    float alpha = smoothstep(
        chroma_threshold - chroma_padding,
        chroma_threshold + chroma_padding, 
        dot(diff,diff));
    return alpha;
}

vec4 diffuse_lighting(in Object object, in Ray ray, in Texture object_texture, in vec3 intersect_point, in vec3 normal) {
    vec4 diffuse_color;
    if(object_texture.texture_type == TEX_MAND) {
        diffuse_color = mand(intersect_point.xz, intersect_point, normal);
    } else if(object_texture.texture_type == TEX_GRID) {
        diffuse_color = grid(intersect_point.xz);
    } else if(object_texture.texture_type == TEX_PERLIN) {
        diffuse_color = perlin(intersect_point);
    } else if(object_texture.texture_type == TEX_MANDWARP) {
        diffuse_color = mandwarp(intersect_point, intersect_point, normal);
    } else if(object_texture.texture_type == TEX_FLAT) {
        diffuse_color = vec4(object_texture.tex_diffuse_color, 1.0);
    } else if(object_texture.texture_type == TEX_UV) {
        if(object.type == PLANE_TYPE) {
            vec3 relative = intersect_point - object_texture.tex_uv_center;
            float u = (dot(relative, object_texture.tex_uv_u_dir) + object_texture.tex_uv_u_offset)
                / object_texture.tex_uv_u_scale;
            float v = (dot(relative, object_texture.tex_uv_v_dir) + object_texture.tex_uv_v_offset)
                / object_texture.tex_uv_v_scale;

            // u and v are in [-1.0..1.0] space. re-scale for texture lookup.
            vec2 uv = vec2(u,v) / 2.0 + 0.5;

            diffuse_color = channel_uv_lookup(object_texture.tex_uv_channel, uv);
            if(object_texture.tex_uv_chromakey) {
                diffuse_color.a *= get_chromakey_opacity(diffuse_color);
            }
            diffuse_color.a += 0.0;  // DO NOT REMOVE. This was eliminating some odd math bug that
                                     // made alphas of opaque objects semi-transparent.
            return diffuse_color;
        } else if(object.type == CYLINDER_TYPE) {
            float normal_dot_u = dot(normal, object_texture.tex_uv_u_dir);
            float normal_dot_v = dot(normal, object_texture.tex_uv_v_dir);
            if(abs(normal_dot_v) > 0.99) {
                //TODO: we hit an endcap. Use the first 0.25 and last 0.25 of texmap in v dir.
                return vec4(0.0);
            } else {
                //TODO(OPTIMIZE): use a 3x3 matrix for the scale/xlate/scale of uv.
                float abs_cos_angle = dot(normal, object_texture.tex_uv_center);
                float u = sign(normal_dot_u) *  acos(abs_cos_angle) / PI;
                u = (u + object_texture.tex_uv_u_offset) / object_texture.tex_uv_u_scale;                
                //scale from [-1.0..1.0] to [0.0..1.0]
                u = u / 2.0 + 0.5;

                float v = fract_along_centerline(
                    intersect_point, object.cylinder_cap0_pos, object.cylinder_height, object.cylinder_centerline);

                //scale from [0.0..1.0] to [-1.0..1.0]
                v = (v - 0.5) * 2.0;
                v = (v + object_texture.tex_uv_v_offset) / object_texture.tex_uv_v_scale;
                //scale from [-1.0..1.0] to [0.0..1.0]
                v = v / 2.0 + 0.5;

                vec2 uv = vec2(u,v);
                diffuse_color = channel_uv_lookup(object_texture.tex_uv_channel, uv);
                DEBUG_TRACE(0, diffuse_color);
                DEBUG_TRACE(1, vec4(uv.x, uv.y, 0.0, 0.0));

                if(object_texture.tex_uv_chromakey) {
                    diffuse_color.a *= get_chromakey_opacity(diffuse_color);
                }
            }
        }
    }

    // If it's transparent, let the texturing function generate a new transmission ray:
    if(diffuse_color.a == 0.0) {
        return diffuse_color;
    }

    vec3 lit = vec3(0.0);
    float max_samples = 8.0;
    for(int light_index = 0; light_index < constructed_light_count; light_index++) {
        for(float sample_id = 0.0; sample_id < max_samples; sample_id += 1.0) {
            bool obstructed = false;
            vec3 point_on_light_sphere = lights[light_index].pos + rand_normalized_vector() * lights[light_index].size;
            vec3 to_light = point_on_light_sphere - intersect_point;
            float dist_to_light = length(to_light);
            to_light /= dist_to_light;
            Ray test_ray;
            test_ray.origin = intersect_point + 0.001*normal;
            test_ray.dir = to_light;
            for(int obj_index = 0; obj_index < constructed_obj_count; obj_index++) {
                if(objects[obj_index].casts_shadow) {
                    float dist_to_object = obj_dist(objects[obj_index], test_ray);
                    if(dist_to_object > 0.0 && dist_to_object < dist_to_light) {
                        obstructed = true;
                        break;
                    }
                }
            }
            if(!obstructed) {
                lit += diffuse_color.rgb * lights[light_index].color/max_samples * dot(normal, to_light);
            }
        }
    }
    vec4 retval = vec4(lit, diffuse_color.a + 0.0);  // DO NOT REMOVE THE +0.0. 1.0s are getting mangled.
    return retval;
}

vec3 process_texture(in Object nearest, in Ray ray, in float nearest_dist, vec3 current_contribution) {
    vec3 intersect_point;
    Texture object_texture = textures[nearest.texture_index];

    intersect_point = ray.origin + ray.dir * nearest_dist;

    bool incident_upon_exterior = true;
    vec3 normal = get_normal(nearest, ray.dir, intersect_point, incident_upon_exterior);

    vec4 tex_color;
    if(object_texture.texture_type >= TEX_MIN_DIFFUSE_ID && object_texture.texture_type >= TEX_MIN_DIFFUSE_ID) {
        tex_color = diffuse_lighting(nearest, ray, object_texture, intersect_point, normal);
        if(tex_color.a > 0.995) {
            return tex_color.rgb;
        }

        push_transmissive_ray(ray, intersect_point, current_contribution * (1.0 - tex_color.a));
        return tex_color.rgb * tex_color.a;
    } else if(object_texture.texture_type == TEX_REFLECT) {
        push_reflection_ray(
            ray, normal, intersect_point, current_contribution * object_texture.tex_reflective_attenuation);
    } else if (object_texture.texture_type == TEX_REFRACT) {
        float ior = object_texture.tex_ior;
        if(!incident_upon_exterior) {
            // We've hit the inside of the object. We have to apply transmissive attenuation.
            ior = 1.0/ior;
            /*
            TODO: The glass attenuation comp is broken. I'm getting near-black in the glass tube.
            current_contribution.x *= 
                (1.0 - pow(10.0, -object_texture.tex_transmissive_coefficient.x * nearest_dist));
            current_contribution.y *= 
                (1.0 - pow(10.0, -object_texture.tex_transmissive_coefficient.y * nearest_dist));
            current_contribution.z *= 
                (1.0 - pow(10.0, -object_texture.tex_transmissive_coefficient.z * nearest_dist));
            */
        }
        float reflection_fraction = fresnel_reflection_fraction(ray.dir, normal, ior);
        if(reflection_fraction > 0.002) {
            vec3 reflection_contribution = 
                reflection_fraction * current_contribution * object_texture.tex_reflective_attenuation;
            push_reflection_ray(ray, normal, intersect_point, reflection_contribution);
        } 

        //W W W TTT FFF
        // WWW   T  FF
        // W W   T  F       WHY IS reflection_fraction 0?
        //reflection_fraction = 0.0;
        if(reflection_fraction < 0.998) {
            vec3 refraction_contribution = (1.0 - reflection_fraction) * current_contribution;
            push_refraction_ray(ray, normal, intersect_point, refraction_contribution, ior);
        }
    }

    return vec3(0.0);
}

vec3 trace() {
    Object nearest;
    int nearest_index;

    bool traversed;

    Ray ray;
    vec3 current_contribution = vec3(1.0);

    vec3 accumulated_texture = vec3(0.0);

    int ray_count = 0;

    while(pop(ray, current_contribution)) {
        float nearest_dist = MAX_DIST;
        nearest.type = -1;
        nearest_index = -1;
        for(int obj_index=0; obj_index<=constructed_obj_count; obj_index++) {
            float dist = obj_dist(objects[obj_index], ray);
            if(dist < nearest_dist) {
                nearest = objects[obj_index];
                nearest_dist = dist;
                nearest_index = obj_index;
            }
        }

        if(nearest_dist != MAX_DIST) {
            //return vec3(fract(2.0*nearest_dist));
            accumulated_texture += current_contribution * process_texture(
                nearest, ray, nearest_dist, current_contribution);
        } else {
            //nothing hit
            float sky_blue = ray.dir.y > 0.0 ? ray.dir.y: 0.0;
            float sky_rg = pow(sky_blue, 4.0);
            accumulated_texture += current_contribution * vec3(sky_rg, sky_rg, sky_blue);
        }

        DEBUG_TRACE(2, vec4(nearest_dist));

        // Do a distance check to the light and see if it's closer than the nearest hit object?
        // I need some way to determine if the current ray is in free space before I accumulate
        // the light color for visible lighting.

        for(int light=0; light<constructed_light_count; light++) {
            vec3 to_light = lights[light].pos - ray.origin;
            if(dot(to_light, to_light) < nearest_dist * nearest_dist) {
                float dot_prod = dot(to_light, ray.dir);
                // I used to normalize to_light first, then do the dot_prod check for > 0.0, but that 
                // does a possibly unnecessary sqrt in computing the length for normalization. This avoids that:
                if(dot_prod > 0.0) {
                    float len = length(to_light);
                    to_light /= len;
                    accumulated_texture += lights[light].color * pow(dot_prod/len, 280.0) * current_contribution;
                }
            }
        }


        // REMEMBER that the max ray count is no the max recursion depth, but the max number of
        // rays tested for a single pixel. Two nested spheres can requrie 50 or more at current
        // limits.
        ray_count++;
        DEBUG_RETRACE(3, vec4(ray_count));
        if(ray_count == 25) {
            //return vec3(fract(nearest_dist*2.0));
            //return accumulated_texture;
            break;
        }
    }

    DEBUG_TRACE(1, vec4(accumulated_texture, 1.0));
    return accumulated_texture;
}

#define add_sphere(obj_list,obj_count,max_count,pos,radius,tex_index,shadow)\
    obj_count;  /*To "return constructed object ID"*/ \
    do {\
        if(obj_count<max_count) {\
            obj_list[obj_count].type = SPHERE_TYPE;\
            obj_list[obj_count].sphere_pos = pos;\
            obj_list[obj_count].sphere_radius = radius;\
            obj_list[obj_count].texture_index = tex_index;\
            obj_list[obj_count].casts_shadow = shadow;\
            obj_count++;\
        }\
    }while(false)

#define add_plane(obj_list,obj_count,max_count,normal,displacement,tex_index,shadow)\
    obj_count;  /*To "return constructed object ID"*/ \
    do {\
        if(obj_count<max_count) {\
            obj_list[obj_count].type = PLANE_TYPE;\
            obj_list[obj_count].plane_normal = normal;\
            obj_list[obj_count].plane_displacement = displacement;\
            obj_list[obj_count].texture_index = tex_index;\
            obj_list[obj_count].casts_shadow = shadow;\
            obj_count++;\
        }\
    }while(false)

#define add_cylinder(obj_list,obj_count,max_count,cap0_pos,cap1_pos,radius,tex_index,shadow)\
    obj_count;  /*To "return constructed object ID"*/ \
    do {\
        if(obj_count<max_count) {\
            obj_list[obj_count].type = CYLINDER_TYPE;\
            obj_list[obj_count].cylinder_cap0_pos = cap0_pos;\
            obj_list[obj_count].cylinder_cap1_pos = cap1_pos;\
            obj_list[obj_count].cylinder_centerline = cap1_pos - cap0_pos;\
            obj_list[obj_count].cylinder_height = length(obj_list[obj_count].cylinder_centerline);\
            obj_list[obj_count].cylinder_centerline /= obj_list[obj_count].cylinder_height;\
            obj_list[obj_count].cylinder_radius = radius;\
            obj_list[obj_count].cylinder_height_plus_diameter = obj_list[obj_count].cylinder_height + 2.0 * radius;\
            obj_list[obj_count].texture_index = tex_index;\
            obj_list[obj_count].casts_shadow = shadow;\
            obj_count++;\
        }\
    } while(false)

#define add_tube(obj_list,obj_count,max_count,cap0_pos,cap1_pos,major_radius,minor_radius,tex_index,shadow)\
    obj_count;  /*To "return constructed object ID"*/ \
    do {\
        if(obj_count<max_count) {\
            obj_list[obj_count].type = TUBE_TYPE;\
            obj_list[obj_count].tube_cap0_pos = cap0_pos;\
            obj_list[obj_count].tube_cap1_pos = cap1_pos;\
            obj_list[obj_count].tube_centerline = cap1_pos - cap0_pos;\
            float len = length(obj_list[obj_count].tube_centerline);\
            obj_list[obj_count].tube_centerline /= len;\
            obj_list[obj_count].tube_major_radius = major_radius;\
            obj_list[obj_count].tube_minor_radius = minor_radius;\
            obj_list[obj_count].tube_height_plus_diameter = len + 2.0 * major_radius;\
            obj_list[obj_count].texture_index = tex_index;\
            obj_list[obj_count].casts_shadow = shadow;\
            obj_count++;\
        }\
    } while(false)


int add_tex_refract(in vec3 reflective_attenuation, in vec3 transmissive_coefficient, in float ior) {
    if(constructed_texture_count == max_textures)
        return 0;

    textures[constructed_texture_count].texture_type = TEX_REFRACT;
    textures[constructed_texture_count].tex_reflective_attenuation = reflective_attenuation;
    textures[constructed_texture_count].tex_transmissive_coefficient = transmissive_coefficient;
    textures[constructed_texture_count].tex_ior = ior;
    return constructed_texture_count++;
}

int add_tex_reflect(in vec3 reflective_attenuation) {
    if(constructed_texture_count == max_textures)
        return 0;

    textures[constructed_texture_count].texture_type = TEX_REFLECT;
    textures[constructed_texture_count].tex_reflective_attenuation = reflective_attenuation;
    return constructed_texture_count++;
}

int add_tex_parametric(in int texture_type) {
    if(constructed_texture_count == max_textures)
        return 0;

    textures[constructed_texture_count].texture_type = texture_type;
    return constructed_texture_count++;
}

int add_tex_flat(in int texture_type, in vec3 color) {
    if(constructed_texture_count == max_textures)
        return 0;

    textures[constructed_texture_count].texture_type = TEX_FLAT;
    textures[constructed_texture_count].tex_diffuse_color = color;
    return constructed_texture_count++;
}

int add_uv_tex(
        in vec3 center, in vec3 u_dir, in vec3 v_dir, 
        in float u_scale, in float v_scale,
        in float u_offset, in float v_offset,
        in int channel, in bool chromakey) {
    textures[constructed_texture_count].texture_type = TEX_UV;
    textures[constructed_texture_count].tex_uv_center = center;
    textures[constructed_texture_count].tex_uv_u_dir = normalize(u_dir);
    textures[constructed_texture_count].tex_uv_v_dir = normalize(v_dir);
    textures[constructed_texture_count].tex_uv_u_scale = u_scale;
    textures[constructed_texture_count].tex_uv_v_scale = v_scale;
    textures[constructed_texture_count].tex_uv_u_offset = u_offset;
    textures[constructed_texture_count].tex_uv_v_offset = v_offset;
    textures[constructed_texture_count].tex_uv_channel = channel;
    textures[constructed_texture_count].tex_uv_chromakey = chromakey;

    return constructed_texture_count++;    
}

void add_light(in vec3 pos, in vec3 color, in float size) {
    if(constructed_light_count == max_lights)
        return;

    lights[constructed_light_count].pos = pos;
    lights[constructed_light_count].color = color;
    lights[constructed_light_count].size = size;

    constructed_light_count++;
}

