    float dist_to_pix_plane = 5.0;
    float t = sin(iTime) / 8.0;
    vec3 cam_position = vec3(dist_to_pix_plane * sin(t), 1.2, dist_to_pix_plane * cos(t));
    //cam_position = vec3(0.0, 1.2, dist_to_pix_plane);

    float pix_plane_height = 2.1;
    vec3 cam_look_at = vec3( -1.2 * cos(iTime/2.0), 2.0, 1.2 * sin(iTime/2.0));
    cam_look_at = vec3(0.0, -0.1, 0.0);
    vec3 cam_look_dir = dist_to_pix_plane * normalize(cam_look_at - cam_position);

    vec3 right = cross(vec3(0.0, 1.0, 0.0), cam_look_dir);
    right = (pix_plane_height / 2.0) * normalize(right);
    vec3 up = cross(cam_look_dir, right);
    up = (pix_plane_height / 2.0) * normalize(up);

    vec3 pixel_width = right/iResolution.x;
    vec3 pixel_height = up/iResolution.y;

    // You can get away with an aa level of 4ish if you turn off depth-of-field. Once
    // I have lighting working, the DOF effect won't be so impactful and can be turned
    // down.
    float antialias_level = 4.0;
    float depth_of_field_intensity = 3.0;


    vec3 out_color = vec3(0.0);
    Ray ray;
    for(float aa=0.0; aa<antialias_level; aa+=1.0) {
        vec3 ray_screen_isect_point = cam_position + cam_look_dir + 
            (screen_space.x * right) + (screen_space.y * up);

        ray_screen_isect_point += pixel_width * (rand(uv*(aa+1.0) * 2.0 - 1.0));
        ray_screen_isect_point += pixel_height * (rand(uv*(aa+1.5) * 2.0 - 1.0));

        ray.origin = cam_position;
        if(depth_of_field_intensity > 0.0) {
            ray.origin += depth_of_field_intensity * pixel_width * (rand(uv*(aa+1.0) * 2.0 - 1.0));
            ray.origin += depth_of_field_intensity * pixel_height * (rand(uv*(aa+1.0) * 2.0 - 1.0));                 
        }

        ray.dir = normalize(ray_screen_isect_point - ray.origin);

        push(ray, vec3(1.0));
        out_color += trace();
    }
    out_color /= float(antialias_level);

    // do a bit of fake gamma correcting as we output:
    fragColor = vec4(
        pow(out_color.x, 0.5),
        pow(out_color.y, 0.5),
        pow(out_color.z, 0.5),
        1.0);
}

