bool keydown_event(float keyCode) {
    keyCode = (keyCode + 0.5) / 256.0;
    vec2 uv = vec2(keyCode, 0.5);
    float key = texture(iChannel1, uv).r;

    return key > 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
#if DEBUG
    debug_trace_index = -1;
    debug_trace_occurred = false;
    if(fragCoord.y == 0.5 && fragCoord.x <= 9.5) {
        //////////////////
        // TODO: GET OFF THE MAGIC NUMBERS FOR NUM FIELDS AND MOUSE STORAGE LOCATION!!!!!!!
        //////////////////
        vec4 mouse = get_val_at_index(8.0);
        if(fragCoord.x < 8.5) {
            debug_trace_index = int(fragCoord.x);
            render(fragColor, mouse_to_fragCoord(mouse));
        } else if(fragCoord.x == 8.5) {
            //////
            // Sorry, but I had to choose between fidelity and speed. I can make it so that
            // WASD will move by one pixel every frame, but it's very hard to adjust by just
            // one pixel in that case. By looking at the keyboard texture's indication that
            // a key went down in this frame and only moving on those events, it is easy to
            // adjust by a single pixel. My first attempt at this was to count the number of
            // frames since the last keystroke and do some math with the iFrameDelta to make
            // the thing move only every 500ms, but it seems that when a shader is paused,
            // it stops rendering entirely unless it gets a keyboard or mouse event. Having
            // a key or button held down is not an event. Only going up or going down are,
            // so I have no way of checking how long a key has been held down before moving
            // the cursor, so we're currently stuck with the limited cursor motion mechanism
            // we have here:
            if(iMouse.z > 0.0) {
                fragColor = iMouse;
            } else {
                fragColor = mouse;
                fragColor.w += 1.0;
                // I'm a dvorak user, so I support WASD or ",AOE". A is the same on both.
                if(keydown_event(38.0) || keydown_event(87.0) || keydown_event(188.0))   //  W ,
                    fragColor.y += 1.0;
                if(keydown_event(37.0) || keydown_event(65.0))                           //  A
                    fragColor.x -= 1.0;
                if(keydown_event(40.0) || keydown_event(79.0) || keydown_event(83.0))    //  S O
                    fragColor.y -= 1.0;
                if(keydown_event(39.0) || keydown_event(68.0) || keydown_event(69.0))    //  D E
                    fragColor.x += 1.0;             
            }
        } 
    } else {
        render(fragColor, fragCoord);
    }

    if(debug_trace_index >= 0) {
        if(debug_trace_occurred) {
            fragColor = debug_trace_value;
        } else {
            fragColor = vec4(reserved_float);
        }
    }

#else
    render(fragColor, fragCoord);
#endif
}
