float map(vec3 p)

{
    float d2 =sin(-p.y) - cos(p.y) ;    
    p.x = abs(p.x * 1.004);
    vec3 q = fract(p) * 2. - 1.;
	float s  = texelFetch( iChannel0, ivec2(1,0), 0 ).x; 
    q.y = s * 1.;q.y *= 0.0001;q.y /= 0.001;
    float d = length(q) - .07;   
    return max(d,d2);
}

float trace(vec3 o, vec3 r)
{
	float t = 0.0;
    for(int i = 0; i < 40; ++i)
    {
    	vec3 p = o + r * t;float d = map(p);
        t += d * .5;   
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;	
    uv = uv * 2.0 -1.0;uv.x *= iResolution.x / iResolution.y;    
    vec3 r = normalize(vec3(uv,1.0));
    float fft  = texelFetch( iChannel0, ivec2(5,0), 0 ).x;
    float fft2  = texelFetch( iChannel0, ivec2(0,0), 0 ).y;
    r.x += .25 * sin(iTime);
    vec3 o = vec3(0.0, 1., -3.0 + iTime );
    float t = trace(o,r);
    float fog = 1. / (1.0 + t * t * 0.0100);
    vec3 fc = vec3(fog * fft + sin(fft2), fog * 1.2 , fog + 0.23);

    // Output
    fragColor = vec4(fc,1.0);
}
